import React, { useEffect, useState } from "react";
import axios from "axios";

const CourseList = (props) => {
  const [courses, setCourses] = useState([]);
  const [studentCourses, setStudentCourses] = useState({});

  const studentId = props.studentId;
  useEffect(() => {
    // Fetch courses for the student
    axios
      .get("http://localhost:5000/courses")
      .then((response) => {
        setCourses(response.data);
      })
      .catch((error) => {
        console.error("Error fetching courses:", error);
      });

    // Fetch student's course pass statuses
    axios
      .get(`http://localhost:5000/student_courses?student_id=${studentId}`)
      .then((response) => {
        const passedCourses = response.data.reduce(
          (acc, { course_name, passed }) => {
            acc[course_name] = passed;
            return acc;
          },
          {}
        );
        setStudentCourses(passedCourses);
      })
      .catch((error) => {
        console.error("Error fetching student courses:", error);
      });
  }, []);

  // Handle course pass/unmark action
  const handlePassClick = (courseId, courseName) => {
    const passedStatus = !studentCourses[courseName];

    axios
      .post("http://localhost:5000/update_passed_course", {
        student_id: studentId,
        course_id: courseId,
        passed: passedStatus,
      })
      .then(() => {
        setStudentCourses((prevState) => ({
          ...prevState,
          [courseName]: passedStatus,
        }));
        alert(
          `Course ${courseName} marked as ${
            passedStatus ? "Passed" : "Not Passed"
          }`
        );
      })
      .catch((error) => {
        console.error("Error updating pass status:", error);
      });
  };

  return (
    <div>
      <h2>Courses</h2>
      <table>
        <thead>
          <tr>
            <th>Course Name</th>
            <th>ECTS</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {courses.map((course) => (
            <tr key={course.id}>
              <td>{course.name}</td>
              <td>{course.ects}</td>
              <td>{studentCourses[course.name] ? "Passed" : "Not Passed"}</td>
              <td>
                <button onClick={() => handlePassClick(course.id, course.name)}>
                  {studentCourses[course.name]
                    ? "Unmark Passed"
                    : "Mark as Passed"}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CourseList;
