import React, { useEffect, useState } from "react";
import axios from "axios";

const CourseList = (props) => {
  const userId = props.userId;

  const [courses, setCourses] = useState([]);
  const [studentCourses, setStudentCourses] = useState({});

  useEffect(() => {
    axios
      .get(`http://localhost:5000/student_courses?user_id=${userId}`)
      .then((response) => {
        const passedCourses = response.data.reduce(
          (acc, { course_name, passed }) => {
            acc[course_name] = passed;
            return acc;
          },
          {}
        );
        setStudentCourses(passedCourses);
      })
      .catch((error) => {
        console.error("Error fetching student courses:", error);
      });
  }, [userId]);

  return (
    <div>
      <h2>Courses</h2>
      <tbody>
        {courses.map((course) => (
          <tr key={course.id}>
            <td>{course.name}</td>
            <td>{course.ects}</td>
            <td>{studentCourses[course.name] ? "Passed" : "Not Passed"}</td>
            <td>
              <button onClick={() => handlePassClick(course.id, course.name)}>
                {studentCourses[course.name]
                  ? "Unmark Passed"
                  : "Mark as Passed"}
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </div>
  );
};

export default CourseList;
