import React, { useState } from "react";
import Login from "../pages/Login";

function LoginPage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const onLogin = () => {
    console.log("User logged in!");
    setIsLoggedIn(true); // Example action after login
  };

  const [username, setUsername] = useState(null);

  // Check token on component mount
  useEffect(() => {
    const token = localStorage.getItem("authToken");

    if (token) {
      // Decode the token and extract the username
      const decodedToken = jwtDecode(token);
      setUsername(decodedToken.username); // Assuming the username is stored in the token
    } else {
      setUsername(null);
    }
  }, []);

  return (
    <div>
      {isLoggedIn ? (
        <h1>Welcome, {username}</h1> // Display username if token is present
      ) : (
        <li>{/* Show login link if no token */}</li>
      )}
    </div>
  );
}

export default LoginPage;
