import React, { useEffect, useState } from "react";
import axios from "axios";

const CourseList = ({ userId }) => {
  const [courses, setCourses] = useState([]);
  const [studentCourses, setStudentCourses] = useState({});
  console.log(userId);
  useEffect(() => {
    axios
      .get(`http://localhost:5000/courses?user_id=${userId}`)
      .then((response) => {
        const passedCourses = response.data;
        setStudentCourses(passedCourses);
      })
      .catch((error) => {
        console.error("Error fetching student courses:", error);
      });
  }, []);

  return (
    <div>
      <h2>Courses</h2>
      <tbody>
        {courses.map((course) => (
          <tr key={course.id}>
            <td>{course.name}</td>
            <td>{course.ects}</td>
            <td>{studentCourses[course.name] ? "Passed" : "Not Passed"}</td>
            <td>
              <button>
                {studentCourses[course.name]
                  ? "Unmark Passed"
                  : "Mark as Passed"}
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </div>
  );
};

export default CourseList;
